import React from 'react';
import './Cards.module.css';


const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {

  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div className="coronaContainer">
      {/* <div> Following Information up to date: {new Date(lastUpdate).toDateString()}</div> */}
      <div>Infected: {confirmed.value}</div>
      <div>Recovered: {recovered.value}</div>
      <div>Deaths: {deaths.value}</div>
    </div>
  );
};
export default Info;
