import React from 'react';
import './Cards.module.css';

HorizontalBarGraph = function(el, series) {
  this.el = d3.select(el);
  this.series = series;
};

const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {


  HorizontalBarGraph.prototype.draw = function() {
    var x = d3.scale.linear()
      .domain([0, d3.max(this.series, function(d) { return d.value })])
      .range([0, 100]);

    var segment = this.el
      .selectAll(".horizontal-bar-graph-segment")
        .data(this.series)
      .enter()
        .append("div").classed("horizontal-bar-graph-segment", true);

    segment
      .append("div").classed("horizontal-bar-graph-label", true)
        .text(function(d) { return d.label });

    segment
      .append("div").classed("horizontal-bar-graph-value", true)
        .append("div").classed("horizontal-bar-graph-value-bar", true)
          .style("background-color", function(d) { return d.color })
          .text(function(d) { return d.inner_label ? d.inner_label : "" })
          .transition()
            .duration(1000)
            .style("min-width", function(d) { return x(d.value) + "%" });

  };

  var graph = new HorizontalBarGraph('#my-graph', [
    {label: "Desktop", inner_label: "1,167 visits", value: {confirmed}, color: "#6ea6df" },
    {label: "Mobile",  inner_label: "543 visits",   value: 543,  color: "#84c26d" },
    {label: "Tablet",  inner_label: "224 visits",   value: 224,  color: "#e17a69" }
  ]);
  graph.draw();

  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div>
      <div className="horizontal-bar-graph" id="my-graph"></div>
    </div>
  );
};
export default Info;
