import React from 'react';
import CountUp from 'react-countup';

import styles from './Cards.module.css';


const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {
  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <section className={styles.container}>

        <div>
            This Data based on: {new Date(lastUpdate).toDateString()} stats. 
        </div>
        <div className="bar-chart primary" data-total="76" animated>
          <span className="bar-chart--inner" style="width:76%;"></span>
          <span className="bar-chart--text">Infected</span>
        </div>

        <div className="bar-chart secondary" data-total="42" animated>
          <span className="bar-chart--inner" style="width:42%;"></span>
          <span className="bar-chart--text">Recovered</span>
        </div>

        <div className="bar-chart tertiary" data-total="82" animated>
          <span className="bar-chart--inner" style="width:82%;"></span>
          <span className="bar-chart--text">Deaths</span>
        </div>


          {/* <div>
              <CountUp start={0} end={confirmed.value} duration={2.75} separator="," />
              <CountUp start={0} end={recovered.value} duration={2.75} separator="," />
              <CountUp start={0} end={deaths.value} duration={2.75} separator="," />
        </div> */}

    </section>
  );
};
export default Info;
