import React from 'react';
import './Cards.module.css';


const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {

  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div className="coronaContainer">
      {/* <div>Following Information up to date:</div> */}
      <div> Data from {new Date(lastUpdate).toDateString()}</div>
      <pre>Infected : {confirmed.value}</pre>
      <pre>Recovered: {recovered.value}</pre>
      <pre> Deaths  : {deaths.value}</pre>
    </div>
  );
};
export default Info;
