import React from 'react';
import {Typography, Grid } from '@material-ui/core';
import CountUp from 'react-countup';

import styles from './Cards.module.css';

const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {
  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div className={styles.container}>
      <Typography color="textSecondary">
          This Data based on: {new Date(lastUpdate).toDateString()} stats. 
      </Typography>
      <Grid>
        <div>
          <div>
              Infected
          </div>
          <div>
              Infected<CountUp start={0} end={confirmed.value} duration={2.75} separator="," />
              Recovered<CountUp start={0} end={recovered.value} duration={2.75} separator="," />
              Deaths<CountUp start={0} end={deaths.value} duration={2.75} separator="," />

          </div>
        </div>
