import React from 'react';
import styles from './Cards.module.css';

const Info = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {

  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div className={styles.container}>
      <div className = {styles.aboutareafont2}>Following Information up to date:</div>
      <div className = {styles.aboutareafont2}>{new Date(lastUpdate).toDateString()}</div>
        <div className = {styles.aboutareafont2}>Infected : {confirmed.value}</div>
        <div className = {styles.aboutareafont2}>Recovered: {recovered.value}</div>
        <div className = {styles.aboutareafont2}> Deaths  : {deaths.value}</div>
    </div>
  );
};
export default Info;
